        return new Source("{% extends 'base.html.twig' %}

{% block title %}Register{% endblock %}

{% block body %}
<div class=\"container mt-5\">
    <div class=\"row justify-content-center\">
        <div class=\"col-md-6 col-lg-5\"> {# Adjust the column width using Bootstrap grid classes #}
            <h1 class=\"mb-4 text-center\">Register</h1>

            {% if form_errors(registrationForm) %}
                <div class=\"alert alert-danger\">
                    {{ form_errors(registrationForm) }}
                </div>
            {% endif %}

            {{ form_start(registrationForm, {'attr': {'class': 'needs-validation', 'novalidate': 'novalidate'}}) }}

                <div class=\"form-group\">
                    {{ form_label(registrationForm.email, 'Email Address', {'label_attr': {'class': 'form-label'}}) }}
                    {{ form_widget(registrationForm.email, {'attr': {'class': 'form-control'}}) }}
                    {{ form_errors(registrationForm.email) }}
                </div>

                <div class=\"form-group\">
                    {{ form_label(registrationForm.plainPassword, 'Password', {'label_attr': {'class': 'form-label'}}) }}
                    {{ form_widget(registrationForm.plainPassword, {'attr': {'class': 'form-control'}}) }}
                    {{ form_errors(registrationForm.plainPassword) }}
                </div>

                <div class=\"form-check mb-3\">
                    {{ form_widget(registrationForm.agreeTerms, {'attr': {'class': 'form-check-input'}}) }}
                    {{ form_label(registrationForm.agreeTerms, 'I agree to the terms and conditions', {'label_attr': {'class': 'form-check-label'}}) }}
                    {{ form_errors(registrationForm.agreeTerms) }}
                </div>

                <button type=\"submit\" class=\"btn btn-primary btn-block\">Register</button>

            {{ form_end(registrationForm) }}

            <div class=\"text-center mt-3\">
                <a class=\"btn btn-primary btn-lg\" href=\"{{ path('flashcards_all') }}\">Flashcards</a>
            </div>
        </div>
    </div>
</div>
{% endblock %}
", "registration/register.html.twig", "/home/grodel/Desktop/flashcards-php/templates/registration/register.html.twig");
